import React, { useContext, useEffect, useState } from "react";
import { createContext } from "react";
import { PizzaContext } from "./barcha-pitsalar";

export const FilterPizzaContext = createContext([]);

const FilterPizzaContextComp = ({ children }) => {
  const [data, setData] = useContext(PizzaContext);
  const [dataFilter, setDataFilter] = useState(data);
  return (
    <FilterPizzaContext.Provider value={[dataFilter, setDataFilter]}>
      {children}
    </FilterPizzaContext.Provider>
  );
};

export default FilterPizzaContextComp;
